select sql_calc_found_rows
from payment_pointback_record as PPR inner join member as M on (PPR.memberNo = M.memberNo) inner join payment_transaction as VPT on (PPR.paymentTransactionNo = VPT.paymentTransactionNo) inner join affiliate as AF on (VPT.affiliateNo = AF.affiliateNo) where 1 = 1
order by paymentPointbackRecordNo asc;


