	set @node_type := #{nodeType};    
	/* param - 3. 4. 5. 7. ( null) */
  set @node_no := #{nodeNo};     
   /* param - ( null)*/
  set @member_no := #{memberNo}; 

   /* ex) node_type=2, node_no=1, member_no=1 :  1 1  -   . */
   /* ex) node_type=2, node_no=null, member_no=1 :  1  - .  */
   /* ex) node_type=5, node_no=1, member_no=null : 1   - ,, . */
   /* ex) node_type=4, node_no=1, member_no=null : 1  -, . */
   /* ex) node_type=3, node_no=1, member_no=null : 1  -  . */
   /* ex) node_type=7, node_no=1, member_no=null : 1   - .  */
   /* ex)     NULL     

  set @node_id := concat(case when @node_type< 3 then '5' else @node_type end,'-',@node_no);
  set @L := 0;

  select node_type,node_no,up_node_no,recommenderNo, saleManagerNo from(
  select * from node_tree where node_id in(
     select _node_id from(   
        SELECT @node_id AS _node_id,         
              (SELECT @node_id := up_node_id FROM node_tree WHERE node_id = _node_id) AS upper_id, 
              (@L := @L + 1) AS depth
        FROM node_tree AS h
        WHERE @node_id is not null
     )a
   )
   and @node_no is not null
  union all      
  select case when isRecommender='Y' then '2' else '1' end node_type,   concat(case when isRecommender='Y' then '2' else '1' end,'-', a.memberNo) node_id,   @node_id up_node_id,a.memberNo, @node_no, memberName name , a.recommenderNo, saleManagerNo  from member a left outer join recommender b on a.recommenderNo = b.recommenderNo
  where a.memberNo=@member_no
   and @node_type in (1,2)
   and @node_type = case when isRecommender='Y' then '2' else '1' end 
   )a

select sql_calc_found_rows
from my_gift_card as G inner join member as M on (G.memberNo = M.memberNo) inner join gift_card_issue as A on (G.giftCardIssueNo= A.giftCardIssueNo)
where 1 = 1

select sql_calc_found_rows
from affiliate_tid as AT inner join affiliate as A on (AT.affiliateNo= A.AffiliateNo)
where 1 = 1

SELECT gpointPaymentNo, memberNo, memberName, memberEmail, memberPhone, affiliateNo, affiliateSerial, affiliateName, paymentApprovalAmount, paymentApprovalNumber, paymentApprovalStatus, paymentMethod, paymentTransactionType, paymentApprovalDateTime, realPaymentAmount, gpointPaymentAmount, createTime, updateTime
FROM gpoint_payment
where 1 = 1 AND paymentApprovalDateTime LIKE CONCAT(#{searchDate},'%') AND paymentTransactionType = #{searchPaymentTransactionType} AND paymentApprovalStatus = #{searchPaymentApprovalStatus} AND affiliateNo = #{searchAffiliate}
order by ${order}
order by createTime desc
limit ${pageSize} offset ${offset}

select A.pointCouponTransactionNo, A.memberNo, A.pointBackStatus, B.* from point_coupon_transaction as A inner join point_coupon as B on (A.pointCouponNo= B.pointCouponNo)
where 1 = 1 AND pointCouponTransactionNo = #{pointCouponTransactionNo,jdbcType=INTEGER} AND B.pointCouponNo = #{pointCouponNo,jdbcType=INTEGER}
AND couponNumber = #{couponNumber,jdbcType=VARCHAR}
